@credmark.model.it(slug='usdc-holding',
                   version='1.0',
                   display_name='account balance for USDC',
                   description='account balance for USDC')
class USDCHolding(credmark.model.Model):

    def run(self, input) -> dict:
        alice = '0x7E5F4552091A69125d5DfCb7b8C2659029395Bdf'

        usdc_contract = self.context.web3.eth.contract(
            address=self.context.web3.toChecksumAddress(usdc_address),
            abi=usdc_abi)

        account = '0xbdfa4f4492dd7b7cf211209c4791af8d52bf5c50'
        account = '0x9c5083dd4838e120dbeac44c052179692aa5dac5'

        quote = usdc_contract.functions.balanceOf(
            self.context.web3.toChecksumAddress(account)).call()

        result = {'value': quote / 10 ** 18}
        return result
